/*
Reorder comments fields
*/
add_filter( 'comment_form_fields', 'reorder_comment_form_fields' );
function reorder_comment_form_fields( $fields ) {
 $comment = $fields['comment'];
  unset( $fields['comment'] );
  $fields['comment'] = $comment;
  return $fields;
}

 // Comment list
 function walili_comments($comment, $args, $depth){
     $GLOBALS['comment'] = $comment;
     extract($args, EXTR_SKIP);
     ?>
     <div class="media comment">
         <?php
         if(get_avatar($comment)) : ?>
         <div class="media-left">
             <?php echo get_avatar($comment, 70, null, null, array('class'=> 'img-circle')); ?>
         </div>
         <?php endif; ?>
         <div class="media-body">
             <h5 class="commenter-name"> <?php comment_author(); ?> </h5>
             <h6 class="comment_time"> <?php comment_time(get_option('date_format')); ?> </h6>
             <?php comment_text(); ?>
             <?php comment_reply_link(array_merge( $args, array('reply_text'=>esc_html__('REPLY', 'walili').'<i class="fa fa-long-arrow-right"></i>', 'depth' => $depth, 'max_depth' => $args['max_depth']))); ?>
         </div>
     </div>
 <?php
 }

