
(function($){
  'use strict';
   //preloading
    $(window).on('load', function () {
        if ($('.main-preloader').length > 0)
        {
            $('.main-preloader').fadeOut('slow');
        }
    });

    $(window).on('scroll', function () {
      //sticky header
      var height = $(window).scrollTop();
      var isSticky = $('#main-nav').hasClass('sticky');
      if (height > 0) {
         if (isSticky) {
           $('#main-nav').addClass('fixed-top');
           $('#header-main-logo').hide();
           $('#sticky-main-logo').show();
           $('#header_button_form').addClass('sticky');
          }
      }else{
        if (isSticky) {
          $('#main-nav').removeClass('fixed-top');
          $('#header-main-logo').show();
          $('#sticky-main-logo').hide();
          $('#header_button_form').removeClass('sticky');
         }
      }

      //Back to top
      if (height > 600) {
          $('.back-to-top').fadeIn();
      } else {
          $('.back-to-top').fadeOut();
      }
      });
     //Back to top smooth
      $('.back-to-top').click(function () {
        $('body,html').animate({
          scrollTop: 0
        }, 800);
        return false;
      });

      //Header search overlay open
      $('#header-search-icon').on('click', function (){
          $('#myOverlay').slideDown();
          setTimeout(function (){
          $('.top-search-field').focus();
          }, 500);
          //return false;
      });

      //Header search overlay close
      $('#searchOverlayClose').on('click', function (){
          $('#myOverlay').slideUp();
          return false;
      });

      //navbar collapse
      $('.navbar-toggler').on('click', function () {
         $(this).toggleClass('active');
      });

      $('.navbar-toggler.active').on('click', function () {
          $('.navbar-toggler').removeClass('active');
      });

       //dropdown menu
      if($(window).width() < 992){
        $('.navbar-nav li.dropdown > a').on('click', function (event){
          event.preventDefault()
          $(this).parent().find('ul').first().toggle(500);
          $(this).parent().siblings().find('ul').hide(500);
        });
      }

      //padding left widgets
      var widgetsCount = 4;
      $('.footer-widget').each(function( index, element ) {
        var myClassName = element.className;
        if (myClassName.indexOf("col-md-6") >= 0){
          widgetsCount = 2;
        }else if(element.className.indexOf("col-md-4") >= 0){
          widgetsCount = 3;
        }
        else if(element.className.indexOf("col-md-3") >= 0){
          widgetsCount = 4;
        }
        if (index > 0 && index % widgetsCount != 0 && $( window ).width() > 768) {
          $( element ).css( 'padding-left', '70px' );
        }
      });

      //shop list grid
      $('#orderby-list').on('click', function () {
        var globalDiv = $('.shop-main-section .shop-section .products #shop-archive-list-grid');
        var myClassName = globalDiv.attr('class');
        if (myClassName == "col-md-12 col-sm-12 p-2"){
          //list
        }else{
          //grid
          globalDiv.removeClass(myClassName);
          globalDiv.addClass("col-md-12 col-sm-12 p-2");
          $('.shop-list-container').addClass('container');
          $('.shop-list-row').addClass('row');
          $('.shop-list-class4').addClass('col-md-4 col-sm-12');
          $('.shop-list-class8').addClass('col-md-8 col-sm-12');
          $('.shop-product-description-id').css( 'display', 'block' );
        }
      });

      $('#orderby-grid').on('click', function () {
        var globalDiv = $('.shop-main-section .shop-section .products #shop-archive-list-grid');
        var myClassName = globalDiv.attr('class');
        if (myClassName == "col-md-12 col-sm-12 p-2"){
          //list
          globalDiv.removeClass(myClassName);
          globalDiv.addClass("col-md-4 col-sm-12 p-2");
          $('.shop-list-container').removeClass('container');
          $('.shop-list-row').removeClass('row');
          $('.shop-list-class4').removeClass('col-md-4 col-sm-12');
          $('.shop-list-class8').removeClass('col-md-8 col-sm-12');
          $('.shop-product-description-id').css( 'display', 'none' );
        }else{
          //grid
        }
      });

     //Bubbles animation
     if (document.getElementById("bubbles-animation")) particlesJS("bubbles-animation", {
       "particles": {
           "number": {
               "value": 80,
               "density": {
                   "enable": !0,
                   "value_area": 4000
               }
           },
           "color": {
               "value": ["#FFFFFF", "#FFFFFF", "#FFFFFF"]
           },
           "shape": {
               "type": "circle",
               "stroke": {
                   "width": 0,
                   "color": "#fff"
               },
               "polygon": {
                   "nb_sides": 5
               },
               "image": {
                   "src": "img/github.svg",
                   "width": 33,
                   "height": 33
               }
           },
           "opacity": {
               "value": 0.15,
               "random": !0,
               "anim": {
                   "enable": !0,
                   "speed": 0.5,
                   "opacity_min": 0.15,
                   "sync": !1
               }
           },
           "size": {
               "value": 50,
               "random": !0,
               "anim": {
                   "enable": !0,
                   "speed": 3,
                   "size_min": 5,
                   "sync": !1
               }
           },
           "line_linked": {
               "enable": !1,
               "distance": 150,
               "color": "#ffffff",
               "opacity": 0.4,
               "width": 1
           },
           "move": {
               "enable": !0,
               "speed": 1,
               "direction": "top",
               "random": !0,
               "straight": !1,
               "out_mode": "out",
               "bounce": !1,
               "attract": {
                   "enable": !1,
                   "rotateX": 600,
                   "rotateY": 600
               }
           }
       },
       "interactivity": {
           "detect_on": "canvas",
           "events": {
               "onhover": {
                   "enable": !1,
                   "mode": "bubble"
               },
               "onclick": {
                   "enable": !1,
                   "mode": "repulse"
               },
               "resize": !0
           },
           "modes": {
               "grab": {
                   "distance": 400,
                   "line_linked": {
                       "opacity": 1,
                   }
               },
               "bubble": {
                   "distance": 250,
                   "size": 0,
                   "duration": 2,
                   "opacity": 0,
                   "speed": 4
               },
               "repulse": {
                   "distance": 400,
                   "duration": 0.5
               },
               "push": {
                   "particles_nb": 4
               },
               "remove": {
                   "particles_nb": 2
               }
           }
       },
       "retina_detect": !0
   });




})(jQuery);
